  function makeRequestId(bytes32 _keyHash, uint256 _vRFInputSeed) internal pure returns (bytes32) {
    return keccak256(abi.encodePacked(_keyHash, _vRFInputSeed));
  }
}

contracts/ScoutsUpgradeable.sol.content:
pragma solidity ^0.8.0;
// SPDX-License-Identifier: (CC-BY-NC-ND-3.0)
// Code and docs are CC-BY-NC-ND-3.0

import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721EnumerableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "metasoccer-vouchers/contracts/Vouchers.sol";
import "./EntropyManager.sol";

contract YouthScoutsUpgradeable is ERC721EnumerableUpgradeable, AccessControlUpgradeable {
  using Strings for uint256;

  // TODO - add seed

  mapping(uint256 => string) public tokenGenerator;
  EntropyManager public entropyManager;
  bytes32 public MINTER_ROLE;

  string public beginning_uri;
  string public ending_uri;
  string public contract_uri;

  function initialize(string memory _description, string memory _name, address _entropyManager) initializer public {
    __ERC721_init(_description, _name);
    entropyManager = EntropyManager(_entropyManager);
    contract_uri = "https://metadata.metassocer.com/metadata/scouts/collection.json";
    beginning_uri = "https://metadata.metassocer.com/metadata/scouts/";
    ending_uri = ".json";
    _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
    MINTER_ROLE = keccak256("MINTER_ROLE");
  }

  // Minting should be called by external contract/account with minter role
  function mintScout(address _owner, uint256 _tokenId, string memory _tokenGenerator, uint8 _randomSeeds) external onlyRole(MINTER_ROLE) {
    require(_owner != address(0), "Invalid owner address");
    require(bytes(_tokenGenerator).length > 0, "tokenGenerator param missing");
    tokenGenerator[_tokenId] = _tokenGenerator;
    for(uint i = 0; i < _randomSeeds; i++){
      entropyManager.requestEntropy(address(this), _tokenId, i);
    }
    _safeMint(_owner, _tokenId);
  }

  function supportsInterface(bytes4 _interfaceId) public view virtual override(ERC721EnumerableUpgradeable, AccessControlUpgradeable) returns (bool) {
    return super.supportsInterface(_interfaceId);
  }

  // TO DO - add seed to URI? Remove generator!
  function tokenURI(uint256 _tokenId) public view virtual override returns (string memory) {
    return string(abi.encodePacked(beginning_uri, _tokenId.toString(), ending_uri, "?generator=", tokenGenerator[_tokenId]));
  }

  function contractURI() public view returns (string memory) {
    return contract_uri;
  }

  function setBeginningURI(string memory _new_uri) external onlyRole(DEFAULT_ADMIN_ROLE) {
    beginning_uri = _new_uri;
  }

  function setEndingURI(string memory _new_uri) external onlyRole(DEFAULT_ADMIN_ROLE) {
    ending_uri = _new_uri;
  }

  function setContractURI(string memory _new_uri) external onlyRole(DEFAULT_ADMIN_ROLE) {
    contract_uri = _new_uri;
  }

  function burnToken(uint256 _token_id) external {
    require(_isApprovedOrOwner(msg.sender, _token_id), "Sender is not owner nor approved");
    _burn(_token_id);
  }

}

