contracts/TicketsRedeemer.sol.content:
pragma solidity ^0.8.0;
// SPDX-License-Identifier: (CC-BY-NC-ND-3.0)
// Code and docs are CC-BY-NC-ND-3.0

import "@openzeppelin/contracts/access/AccessControl.sol";
import "metasoccer-vouchers/contracts/Vouchers.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "./ScoutsIdGenerator.sol";
import "./Scouts.sol";

contract TicketsRedeemer is AccessControl {
  using Strings for uint256;

  bytes32 public constant REDEEMER_ROLE = keccak256("REDEEMER_ROLE");

  event TicketRedeemed(uint256 indexed ticketId, uint256 indexed scoutId, address indexed owner);

  uint8 public requiredSeeds;
  Vouchers public ticketsContract;
  YouthScouts public scoutsContract;
  ScoutsIdGenerator public idGenerator;

  constructor(address _scoutsAddress, address _ticketsAddress, address _idGenerator, uint8 _requiredSeeds) {
    ticketsContract = Vouchers(_ticketsAddress);
    scoutsContract = YouthScouts(_scoutsAddress);
    idGenerator = ScoutsIdGenerator(_idGenerator);
    requiredSeeds = _requiredSeeds;
    _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
  }

  function redeemTicket(uint256 _tokenId) external {
    require(msg.sender == ticketsContract.ownerOf(_tokenId), "Only ticket owner can redeem");
    _redeemTicket(_tokenId);
  }

  function redeemExternalTicket(uint256 _tokenId) external onlyRole(REDEEMER_ROLE) {
    _redeemTicket(_tokenId);
  }

  function bulkRedeem(uint256[] memory _tokenIds) external {
    for (uint16 i = 0; i < _tokenIds.length; i++) {
      uint256 tokenId = _tokenIds[i];
      require(msg.sender == ticketsContract.ownerOf(tokenId), "Only ticket owner can redeem");
      _redeemTicket(tokenId);
    }
  }

  function setIdGenerator(address _newIdGenerator) external onlyRole(DEFAULT_ADMIN_ROLE) {
    idGenerator = ScoutsIdGenerator(_newIdGenerator);
  }

  function _redeemTicket(uint256 _tokenId) internal {
    address ticketOwner = ticketsContract.ownerOf(_tokenId);
    ticketsContract.burnToken(_tokenId);
    string memory tokenGenerator = string(abi.encodePacked("Ticket-", _tokenId.toString()));
    uint256 scoutId = idGenerator.getScoutId("Ticket", _tokenId);
    scoutsContract.mintScout(ticketOwner, scoutId, tokenGenerator, requiredSeeds);
    emit TicketRedeemed(_tokenId, scoutId, scoutsContract.ownerOf(scoutId));
  }

}

contracts/ScoutsIdGenerator.sol.content:
pragma solidity ^0.8.0;
// SPDX-License-Identifier: (CC-BY-NC-ND-3.0)
// Code and docs are CC-BY-NC-ND-3.0


contract ScoutsIdGenerator {

  function getScoutId(string memory _generator, uint256 _tokenId) public pure returns (uint256) {
    bytes32 encodedGenerator = keccak256(abi.encodePacked(_generator)); 
    if (encodedGenerator == keccak256(abi.encodePacked("Ticket"))) {
      // First 4699 scouts reserved for Pioneers
      require(_tokenId < 4700, "Only 4700 Pioneers should be available");
      return(_tokenId);
    } else if (encodedGenerator == keccak256(abi.encodePacked("LootBox"))) {
      // Scouts from LootBoxes shouldn't have an id below 4700
      return(_tokenId + 4700);
    } else {
      revert('invalid input');
    }
  }

}

contracts/Scouts.sol.content:
pragma solidity ^0.8.0;
// SPDX-License-Identifier: (CC-BY-NC-ND-3.0)
// Code and docs are CC-BY-NC-ND-3.0

import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "metasoccer-vouchers/contracts/Vouchers.sol";
import "./EntropyManager.sol";
import "./EntropyStorage.sol";

contract YouthScouts is ERC721Enumerable, EntropyStorage, ReentrancyGuard {
  using Strings for uint256;

  bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
  bytes32 public constant SET_DNA_ROLE = keccak256("SET_DNA_ROLE");
  bytes32 public constant SET_ATTRIBUTES_ROLE = keccak256("SET_ATTRIBUTES_ROLE");

  mapping(uint256 => string) public tokenDNA;
  mapping(uint256 => mapping(string => string)) public tokenAttributes; 
  mapping(uint256 => string) public tokenGenerator;
  EntropyManager public entropyManager;

  string public metadata_uri = "https://metadata.metassocer.com/scouts";

  constructor(string memory _description, string memory _name, address _entropyManager) ERC721(_description, _name) {
    entropyManager = EntropyManager(_entropyManager);
    _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
  }

  function supportsInterface(bytes4 _interfaceId) public view virtual override(ERC721Enumerable, AccessControl) returns (bool) {
    return super.supportsInterface(_interfaceId);
  }

  // Minting should be called by external contract/account with minter role
  function mintScout(address _owner, uint256 _tokenId, string memory _tokenGenerator, uint8 _randomSeeds) external onlyRole(MINTER_ROLE) nonReentrant {
    require(_owner != address(0), "Invalid owner address");
    require(bytes(_tokenGenerator).length > 0, "tokenGenerator param missing");
    tokenGenerator[_tokenId] = _tokenGenerator;
    for(uint i = 0; i < _randomSeeds; i++){
      entropyManager.requestEntropy(address(this), _tokenId, i);
    }
    _safeMint(_owner, _tokenId);
  }

  function tokenURI(uint256 _tokenId) public view virtual override returns (string memory) {
    return string(abi.encodePacked(metadata_uri, "/", _tokenId.toString()));
  }

  function contractURI() public view returns (string memory) {
    return metadata_uri;
  }

  function setDNA(uint256 _tokenId, string memory _dna) external onlyRole(SET_DNA_ROLE) {
    require(bytes(tokenDNA[_tokenId]).length == 0, "DNA already exists");
    tokenDNA[_tokenId] = _dna;
  }

  function setAttribute(uint256 _tokenId, string memory _attribute, string memory _value) external onlyRole(SET_ATTRIBUTES_ROLE) {
    tokenAttributes[_tokenId][_attribute] = _value;
  }

  function burnToken(uint256 _tokenId) external {
    require(_isApprovedOrOwner(msg.sender, _tokenId), "Sender is not owner nor approved");
    _burn(_tokenId);
  }

  function setMetadataURI(string memory _new_uri) external onlyRole(DEFAULT_ADMIN_ROLE) {
    metadata_uri = _new_uri;
  }

  function setEntropyManager(address _entropyManager) external onlyRole(DEFAULT_ADMIN_ROLE) {
    entropyManager = EntropyManager(_entropyManager);
  }
}

contracts/LootBoxesRedeemer.sol.content:
pragma solidity ^0.8.0;
// SPDX-License-Identifier: (CC-BY-NC-ND-3.0)
// Code and docs are CC-BY-NC-ND-3.0

import "metasoccer-vouchers/contracts/Vouchers.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "./Scouts.sol";

contract LootBoxesRedeemer {
  using Strings for uint256;

  event LootBoxRedeemed(uint256 indexed ticketId, uint256 indexed scoutId, address indexed owner);

  uint8 public requiredSeeds;
  Vouchers public boxesContract;
  YouthScouts public scoutsContract;

  constructor(address _scoutsAddress, address _ticketsAddress, uint8 _requiredSeeds) {
    boxesContract = Vouchers(_ticketsAddress);
    scoutsContract = YouthScouts(_scoutsAddress);
    requiredSeeds = _requiredSeeds;
  }

  function redeemLootBox(uint256 _tokenId) external {
    // The first 4700 scouts are reserved for Pioneers
    uint256 scoutId = _tokenId + 4700;
    address boxOwner = boxesContract.ownerOf(_tokenId);
    boxesContract.burnToken(_tokenId);
    string memory tokenGenerator = string(abi.encodePacked("LootBox-", _tokenId.toString()));
    scoutsContract.mintScout(boxOwner, scoutId, tokenGenerator, requiredSeeds);
    emit LootBoxRedeemed(_tokenId, scoutId, scoutsContract.ownerOf(scoutId));
  }

}
